import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import axios from 'axios';

const CheckoutForm = ({ amount }) => { // Include amount as a prop
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: 'card',
      card: elements.getElement(CardElement),
    });

    if (!error) {
      const { id } = paymentMethod;
      try {
        // Convert amount to the smallest currency unit (e.g., cents for USD)
        const paymentAmount = parseInt(amount * 100); // Assuming amount is in dollars

        const { data } = await axios.post('http://localhost:5001/create-payment-intent', { 
          amount: paymentAmount, // Use the passed amount for the payment
          id 
        });

        const confirmed = await stripe.confirmCardPayment(data.clientSecret);
        if (confirmed.paymentIntent.status === 'succeeded') {
          console.log('Payment successful!');
          // Handle successful payment here
        }
      } catch (error) {
        console.log('Payment failed:', error);
        // Handle payment errors here
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <CardElement />
      <button type="submit" disabled={!stripe}>
        Pay
      </button>
    </form>
  );
};

export default CheckoutForm;
