import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';     
import { Form, Button, Card, Container, Row, Col } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import axios from 'axios';
import './Buyer.css';
import { toast } from 'react-toastify';




const Buyer = () => {
    const navigate = useNavigate();
    const [listings, setListings] = useState([]);
    const [searchParams, setSearchParams] = useState({
        departureCity: '',
        destination: '',
        date: new Date(),
    });
    const [filteredResults, setFilteredResults] = useState([]);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    //payment stuff here
    const [selectedListing, setSelectedListing] = useState(null);
    const [userId, setUserId] = useState(null);


    useEffect(() => {
        const token = window.localStorage.getItem("token");
        const userId = window.localStorage.getItem("userId"); // Assuming you store the user's ID in local storage

        console.log("User ID from local storage:", userId); // Add this line to log the user ID
        setUserId(userId);
        if (!token) {
            alert("You must be logged in to access this page");
            navigate("/login");
        } else {
            setIsAuthenticated(true);
            setUserId(userId);
            fetchListings();
        }
    }, [navigate]);

    const fetchListings = async () => {
        try {
            const response = await axios.get('http://localhost:5001/api/listings');
            setListings(response.data);
        } catch (error) {
            console.error("Error fetching data:", error);
            // ... error handling
        }
    };

    const handleInputChange = (e, field) => {
        setSearchParams({ ...searchParams, [field]: e.target.value });
    };

    const handleDateChange = (date) => {
        setSearchParams({ ...searchParams, date: date });
    };

    const handleSearch = () => {
        const results = listings.filter(listing => 
            listing.departureCity.toLowerCase() === searchParams.departureCity.toLowerCase() &&
            listing.destination.toLowerCase() === searchParams.destination.toLowerCase() &&
            new Date(listing.date).toDateString() === searchParams.date.toDateString()
        );
        setFilteredResults(results);
    };

    const handleBuyNow = (listing) => {
        console.log(`Buy Now clicked for listing with ID: ${listing._id}`);
        console.log("Selected listing:", listing); // Add this line to log the selected listing
        setSelectedListing(listing);
        displayRazorpay(listing.askingPrice, listing);
    };



    if (!isAuthenticated) {
        return <div>Loading...</div>;
    }

    function loadScript(src) {
        return new Promise((resolve) => {
            const script = document.createElement("script");
            script.src = src;
            script.onload = () => {
                resolve(true);
            };
            script.onerror = () => {
                resolve(false);
            };
            document.body.appendChild(script);
        });
    }

    async function displayRazorpay(listingPrice, listing) {
        const res = await loadScript("https://checkout.razorpay.com/v1/checkout.js");

        if (!res) {
            alert("Razorpay SDK failed to load. Are you online?");
            return;
        }

        const result = await axios.post("http://localhost:5001/order", { amount: listingPrice });

        if (!result) {
            alert("Server error. Are you online?");
            return;
        }

        const { amount, id: order_id, currency } = result.data;

        const options = {
            key: "rzp_test_0hFrUxLaA2mPxN", // Replace with your Razorpay key
            amount: amount.toString(),
            currency: currency,
            name: listing.name, // Name of the product or service being purchased
            description: `Purchase of ${listing.name}`, // Description of the product or service

            order_id: order_id,
            handler: async function (response) {
                if (!selectedListing || !userId) {
                    console.error("Selected listing or user ID is null");
                    return;
                }
                const data = {
                    orderCreationId: order_id,
                    razorpayPaymentId: response.razorpay_payment_id,
                    razorpayOrderId: response.razorpay_order_id,
                    razorpaySignature: response.razorpay_signature,
                    listingId: selectedListing._id, // ID of the listing being purchased
                    userId: userId // ID of the user making the purchase
                };

                const result = await axios.post("http://localhost:5001/order/success", data);
                console.log(result);
                toast.success("ðŸ¦„ Thanks for the purchase!! !", { onClose: () => navigate("/my-listings")});


            },
            theme: {
                color: "#61dafb",
            },
            prefill: {
                name: "User's Name", // Prefill user's name if available
                email: "user@example.com", // Prefill user's email if available
                contact: "1234567890", // Prefill user's phone number if available
            },
            notes: {
                address: "Razorpay Corporate Office" // Optional: Add a note if required
            },
            modal: {
                ondismiss: function() {
                    console.log("Transaction cancelled by user.");
                }
            }
        };

        const paymentObject = new window.Razorpay(options);
        paymentObject.open();
    }



    return (
        <Container>
            <h1 style={{ marginTop: "50px" }}>Search for Listings</h1>
            <Form>
                <Row>
                    <Col>
                        <Form.Group controlId="formDepartureCity">
                            <Form.Label>Departure City</Form.Label>
                            <Form.Control 
                                type="text" 
                                placeholder="Enter departure city" 
                                value={searchParams.departureCity}
                                onChange={(e) => handleInputChange(e, 'departureCity')} 
                            />
                        </Form.Group>
                    </Col>
                    <Col>
                        <Form.Group controlId="formDestination">
                            <Form.Label>Destination</Form.Label>
                            <Form.Control 
                                type="text" 
                                placeholder="Enter destination" 
                                value={searchParams.destination}
                                onChange={(e) => handleInputChange(e, 'destination')} 
                            />
                        </Form.Group>
                    </Col>
                    <Col>
                        <Form.Group controlId="formDate">
                            <Form.Label>Date</Form.Label>
                            <DatePicker
                                selected={searchParams.date}
                                onChange={handleDateChange}
                                className="form-control"
                            />
                        </Form.Group>
                    </Col>
                </Row>
                <Button variant="primary" onClick={handleSearch}>
                    Search
                </Button>
            </Form>

            <h2 style={{ marginTop: "20px" }}>Listings</h2>
            <Row xs={1} md={2} className="g-4">
                {filteredResults.map((listing, idx) => (
                    <Col key={idx}>
                        <Card>
                            <Card.Body>
                                <Card.Title>{listing.name}</Card.Title>
                                <Card.Text>
                                    Departure City: {listing.departureCity}<br/>
                                    Destination: {listing.destination}<br/>
                                    Date: {new Date(listing.date).toLocaleDateString()}<br/>
                                    Time: {listing.time}<br/>
                                    Available Space: {listing.availableSpace}<br/>
                                    Asking Price: {listing.askingPrice}<br/>
                                    Additional Info: {listing.additionalInfo}
                                </Card.Text>
                            </Card.Body>
                            <Card.Footer>
                            <Button variant="primary" onClick={() => handleBuyNow(listing)}>
                                Buy Now
                            </Button>

                            </Card.Footer>
                        </Card>
                    </Col>
                ))}
            </Row>

        </Container>
    );
};

export default Buyer;
