import React from 'react';
import { Container, Nav, Navbar, NavDropdown } from 'react-bootstrap';

import { Link } from 'react-router-dom';

import 'bootstrap/dist/css/bootstrap.min.css';

import './Header.css'; 

const Header = () => {
  const isLoggedIn = !!window.localStorage.getItem('token');
  const handleLogout = () => {
    window.localStorage.removeItem('token');
    window.location.reload(); // Refresh the page to update the state
};

  return (
    <header className="header fixed-top">
      <Container fluid className="container-xl d-flex align-items-center justify-content-between">
        <Navbar.Brand href="/Home">
          <img src="assets/img/main-icon.png" alt="" />
          <span>LuggShare</span>
        </Navbar.Brand>
        <Navbar expand="lg" id="navbar">
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="/Home">Home</Nav.Link>
              <Nav.Link href="#about">About</Nav.Link>
              <Nav.Link href="#footer">Contact Us</Nav.Link>
              <NavDropdown title="How it works?" id="basic-nav-dropdown">
                <NavDropdown.Item as={Link} to="/seller">I Have Extra Space!</NavDropdown.Item>
                <NavDropdown.Item as={Link} to="/buyer">I Want Extra Space!</NavDropdown.Item>
              </NavDropdown>
              {/* Conditional rendering based on login status */}
              {isLoggedIn ? (
                  <div>
                      <span>Welcome! </span>
                      <button onClick={handleLogout}>Logout</button>
                  </div>
              ) : (
                  <div>
                      <Link to="/login">Login</Link>
                      <Link to="/sign-up">Sign Up</Link>
                  </div>
              )}
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </Container>
      {/* Breadcrumbs */}
      {/* <Container fluid className="container-xl mt-3">
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="index.html">Home</a></li>
          </ol>
        </nav>
      </Container> */}
    </header>
  );
};

export default Header;
