import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom'; // Updated to use useNavigate
import { Button, Form, Modal } from 'react-bootstrap';
import './Seller.css';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import axios from 'axios';

const Seller = () => {
    const navigate = useNavigate(); // Updated to useNavigate
    const initialFormData = {
        name: '',
        email: '',
        departureCity: '',
        destination: '',
        date: new Date(),
        time: '',
        availableSpace: '',
        FlightCarrier: '',
        AskingPrice: '',
        additionalInfo: ''
    };

    const [formData, setFormData] = useState(initialFormData);
    const [errors, setErrors] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        const token = window.localStorage.getItem("token");
        if (!token) {
            alert("You must be logged in to post a listing");
            navigate("/login"); // Updated to use navigate
        } else {
            setIsAuthenticated(true);
        }
    }, [navigate]); // navigate added to the dependency array

    const handleInputChange = (e, field) => {
        setFormData({ ...formData, [field]: e.target.value });
    };

    const handleDateChange = (date) => {
        setFormData({ ...formData, date: date });
    };

    const validateForm = () => {
        let tempErrors = {};
        if (!formData.name) tempErrors.name = "Name is required";
        if (!formData.email) tempErrors.email = "Email is required";
        // Add more validation as needed
        setErrors(tempErrors);
        return Object.keys(tempErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            try {
                const response = await axios.post('http://localhost:5001/api/listings', formData);
                console.log(response.data);
                setFormData(initialFormData);
                setShowModal(true);
            } catch (error) {
                console.error("Error submitting form:", error);
            }
        }
    };

    if (!isAuthenticated) {
        return <div>Loading...</div>;
    }

    return (
        <div className="introTag">
            <h1 style={{ marginTop: "50px" }}>Hi, post your listing</h1>
            <Form onSubmit={handleSubmit}>
                {/* Form Groups for input fields */}
                {/* ... [Rest of your form groups and inputs] */}
                <Button variant="primary" type="submit">
                    Post My Listing
                </Button>
            </Form>
            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Success</Modal.Title>
                </Modal.Header>
                <Modal.Body>Listing was successfully posted!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default Seller;
