// Ensure this line is at the top of your file
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);
console.log(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY); // In your React app

import React from 'react';
import { AuthProvider } from './Components/Authentication/AuthContext';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';


import Dashboard from "./Components/AdminPage/Dashboard";
import Seller from "./Components/Seller/Seller";
import Buyer from "./Components/Buyer/Buyer";
import Header from "./Components/Header/Header";
import Footer from "./Components/Footer/Footer";
import Home from './Components/Landing/landing';
import Login from "./Components/Authentication/login_component";
import SignUp from "./Components/Authentication/signup_component";
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';

// Use your Stripe publishable key
// In your frontend code




function App() {
  const isLoggedIn = window.localStorage.getItem("loggedIn");

  return (
    <Elements stripe={stripePromise}> {/* Wrap the Router with Elements */}
      <AuthProvider>
    <Router>
        <div>
          <Header />
          <Routes>
            <Route exact path="/" element={<Home />} />
            <Route path="/seller" element={<Seller />} />
            <Route path="/login" element={<Login />} />
            <Route path="/sign-up" element={<SignUp />} />
            <Route path="/buyer" element={<Buyer />} />
            <Route path="/admin/dashboard" element={<Dashboard />} />
        </Routes>
          <Footer />
        </div>
      </Router>
      </AuthProvider>
    </Elements>

  );
}

export default App;
